import React, { useEffect, useState } from "react";
import ProductCard from "./ProductCard";
import { useSwipeable } from "react-swipeable";
import "./ProductList.css";

const COLOR_OPTIONS = [
  { key: "yellow", name: "Yellow Gold", code: "#E6CA97" },
  { key: "white", name: "White Gold", code: "#D9D9D9" },
  { key: "rose", name: "Rose Gold", code: "#E1A4A9" },
];

const VISIBLE_COUNT = 4;

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [startIdx, setStartIdx] = useState(0);
  // Filtreler
  const [minPrice, setMinPrice] = useState(0);
  const [maxPrice, setMaxPrice] = useState(50000);
  const [minPopularity, setMinPopularity] = useState(0);
  const [maxPopularity, setMaxPopularity] = useState(5);

  // Debounce için timer ref
  const [debouncedFilters, setDebouncedFilters] = useState({ minPrice, maxPrice, minPopularity, maxPopularity });

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedFilters({ minPrice, maxPrice, minPopularity, maxPopularity });
    }, 500); // 500ms sonra tetiklenir
    return () => clearTimeout(handler);
  }, [minPrice, maxPrice, minPopularity, maxPopularity]);

  useEffect(() => {
    setLoading(true);
    const params = [];
    if (debouncedFilters.minPrice) params.push(`minPrice=${debouncedFilters.minPrice}`);
    if (debouncedFilters.maxPrice < 50000) params.push(`maxPrice=${debouncedFilters.maxPrice}`);
    if (debouncedFilters.minPopularity) params.push(`minPopularity=${debouncedFilters.minPopularity}`);
    if (debouncedFilters.maxPopularity < 5) params.push(`maxPopularity=${debouncedFilters.maxPopularity}`);
    const query = params.length ? `?${params.join("&")}` : "";
    fetch(`/api/products${query}`)
      .then((res) => {
        if (!res.ok) throw new Error("API error");
        return res.json();
      })
      .then((data) => {
        // Her ürüne default selectedColor ekle
        setProducts(data.map((p) => ({ ...p, selectedColor: 0 })));
        setStartIdx(0);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, [debouncedFilters]);

  const maxIdx = Math.max(0, products.length - VISIBLE_COUNT);

  const handlePrev = () => setStartIdx((idx) => Math.max(0, idx - 1));
  const handleNext = () => setStartIdx((idx) => Math.min(maxIdx, idx + 1));

  const swipeHandlers = useSwipeable({
    onSwipedLeft: handleNext,
    onSwipedRight: handlePrev,
    trackMouse: true
  });

  if (loading) return <div className="product-list-container">Loading...</div>;
  if (error) return <div className="product-list-container">Error: {error}</div>;

  return (
    <div className="product-list-container">
      <h2 className="product-list-title">Product List</h2>
      {/* Modern Filtre UI */}
      <div className="filter-section">
        <div className="filter-title">Filtrele</div>
        <div className="filter-row">
          <div className="filter-card">
            <label>Min Price (USD)</label>
            <input type="number" value={minPrice} min={0} max={maxPrice} onChange={e => setMinPrice(Number(e.target.value))} />
          </div>
          <div className="filter-card">
            <label>Max Price (USD)</label>
            <input type="number" value={maxPrice} min={minPrice} max={100000} onChange={e => setMaxPrice(Number(e.target.value))} />
          </div>
          <div className="filter-card">
            <label>Min Popularity</label>
            <input type="number" value={minPopularity} min={0} max={maxPopularity} step={0.1} onChange={e => setMinPopularity(Number(e.target.value))} />
          </div>
          <div className="filter-card">
            <label>Max Popularity</label>
            <input type="number" value={maxPopularity} min={minPopularity} max={5} step={0.1} onChange={e => setMaxPopularity(Number(e.target.value))} />
          </div>
        </div>
      </div>
      <div style={{ display: "flex", alignItems: "center", justifyContent: "center", gap: 8 }}>
        <button onClick={handlePrev} disabled={startIdx === 0} style={{ fontSize: 24, background: "none", border: "none", cursor: "pointer" }}>&lt;</button>
        <div {...swipeHandlers} style={{ display: "flex", gap: "24px", overflow: "hidden", width: 960 }}>
          {loading ? <div>Loading...</div> : products.slice(startIdx, startIdx + VISIBLE_COUNT).map((product, idx) => (
            <ProductCard
              key={product.name}
              product={product}
              colorOptions={COLOR_OPTIONS}
              setProductColor={(colorIdx) => {
                setProducts((prev) => {
                  const copy = [...prev];
                  copy[startIdx + idx] = { ...copy[startIdx + idx], selectedColor: colorIdx };
                  return copy;
                });
              }}
            />
          ))}
        </div>
        <button onClick={handleNext} disabled={startIdx === maxIdx} style={{ fontSize: 24, background: "none", border: "none", cursor: "pointer" }}>&gt;</button>
      </div>
    </div>
  );
};

export default ProductList;
